#Создайте новый каталог. Создайте в этом каталоге файл.import osfolder_name = 'MyFolder'file_name = 'NewFile'file_type = '.txt'path = os.getcwd() + '\\' + folder_nameif not os.path.exists(path):    os.mkdir(path)    print('Создаём каталог ' + folder_name + '...')else:    print('Каталог ' + folder_name + ' уже существует')i = 1while os.path.exists(path+'\\'+file_name+str(i)+file_type):    i+=1file = open(path+'\\'+file_name+str(i)+file_type, 'w')print('Создаём файл ' + file_name+str(i)+file_type + '...')file.close()